{"output":"/**
 * Supabase Client Configuration for Chat Persistence
 * 
 * DOCUMENTATION COMPLIANCE:
 * - Follows AI SDK v5 persistence patterns from /docs/04-ai-sdk-ui/03-chatbot-message-persistence.mdx
 * - Implements server-side and client-side client configurations
 * - Supports existing 24-table enterprise database schema
 * 
 * INTEGRATION POINTS:
 * - Connects with existing user authentication and RLS policies
 * - Utilizes existing database tables: users, user_sessions, ai_interactions
 * - Maintains compatibility with existing database infrastructure
 */

import { createClient, SupabaseClient } from '@supabase/supabase-js';
import { Database } from '../types/database-types';

// Environment configuration validation
const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
const supabaseAnonKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY;
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl) {
  throw new Error('Missing NEXT_PUBLIC_SUPABASE_URL environment variable');
}

if (!supabaseAnonKey) {
  throw new Error('Missing NEXT_PUBLIC_SUPABASE_ANON_KEY environment variable');
}

/**
 * Client-side Supabase client for browser usage
 * Uses anonymous key with RLS policies for security
 */
export const supabaseClient: SupabaseClient<Database> = createClient(
  supabaseUrl,
  supabaseAnonKey,
  {
    auth: {
      autoRefreshToken: true,
      persistSession: true,
      detectSessionInUrl: true,
      flowType: 'pkce'
    },
    realtime: {
      params: {
        eventsPerSecond: 10
      }
    }
  }
);

/**
 * Server-side Supabase client for API routes and server components
 * Uses service role key for full database access (bypasses RLS)
 * Only use in server-side contexts where RLS bypass is required
 */
export const supabaseAdmin: SupabaseClient<Database> = createClient(
  supabaseUrl,
  supabaseServiceKey || supabaseAnonKey, // Fallback to anon key if service key not available
  {
    auth: {
      autoRefreshToken: false,
      persistSession: false
    }
  }
);

/**
 * Safe server-side client that respects RLS policies
 * Use this for most server-side operations where RLS should be enforced
 */
export const supabaseServer: SupabaseClient<Database> = createClient(
  supabaseUrl,
  supabaseAnonKey,
  {
    auth: {
      autoRefreshToken: false,
      persistSession: false
    }
  }
);

/**
 * Chat-specific client configuration with optimized settings
 * Optimized for real-time chat message operations
 */
export const supabaseChatClient: SupabaseClient<Database> = createClient(
  supabaseUrl,
  supabaseAnonKey,
  {
    auth: {
      autoRefreshToken: true,
      persistSession: true,
    },
    realtime: {
      params: {
        eventsPerSecond: 20, // Higher rate for chat operations
      }
    },
    db: {
      schema: 'public'
    }
  }
);

/**
 * Health check function to verify Supabase connection
 * Used for monitoring and debugging
 */
export async function checkSupabaseHealth(): Promise<{
  connected: boolean;
  error?: string;
  responseTime: number;
}> {
  const startTime = Date.now();

  try {
    // Test basic connection with a lightweight query
    const { error } = await supabaseClient
      .from('users')
      .select('id')
      .limit(1);

    const responseTime = Date.now() - startTime;

    if (error) {
      return {
        connected: false,
        error: error.message,
        responseTime
      };
    }

    return {
      connected: true,
      responseTime
    };
  } catch (error) {
    return {
      connected: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      responseTime: Date.now() - startTime
    };
  }
}

/**
 * Get authenticated user from client
 * Helper function for user context in chat operations
 */
export async function getAuthenticatedUser() {
  const { data: { user }, error } = await supabaseClient.auth.getUser();
  return { user, error };
}

/**
 * Initialize user session for chat operations
 * Creates or updates user session for chat persistence tracking
 */
export async function initializeUserSession(userId: string): Promise<{
  success: boolean;
  sessionId?: string;
  error?: string;
}> {
  try {
    // Create or update user session
    const { data, error } = await supabaseClient
      .from('user_sessions')
      .upsert({
        user_id: userId,
        session_data: {
          chat_initialized: true,
          last_activity: new Date().toISOString()
        },
        expires_at: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString(), // 24 hours
        updated_at: new Date().toISOString()
      }, {
        onConflict: 'user_id'
      })
      .select('id')
      .single();

    if (error) {
      return {
        success: false,
        error: error.message
      };
    }

    return {
      success: true,
      sessionId: data.id
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error'
    };
  }
